    private void btnYorumlaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnYorumlaActionPerformed
        String durum1 = "";
        String durum2 = "";
        String durum3 = "";
        if (check_oracle.isSelected() == true) {
            durum1 = "Oracle DBA";

        }

        if (check_java.isSelected() == true) {
            durum2 = "Java Developer";
        }

        if (check_python.isSelected() == true) {
            durum3 = "Python Geli≈ütiricisi";
        }
        lblSonuc.setText("Tittle:" + " " + durum1 + " " + durum2 + " " + durum3);


    }//GEN-LAST:event_btnYorumlaActionPerformed

